import os
import matplotlib.pyplot as plt
import seaborn as sns


def visualize_f1_over_epoch(values, name="mnist", alpha=None):
    os.makedirs("results", exist_ok=True)
    sns.set(style="darkgrid")
    plt.figure(figsize=(8, 5))

    alphas = []
    alpha_queue = []
    if alpha:
        alpha_queue.append(values[alpha])
        alpha.append(alpha)
    else:
        alpha_queue = [values[alpha] for alpha in values]
        alphas = list(values.keys())

    for alpha_ind, queue_item in enumerate(alpha_queue):
        for alg in queue_item:
            label, (x, y) = alg
            sns.lineplot(x=x, y=y, label=label)

        plt.xlabel("Step")
        plt.ylabel("F1-score")
        ax = plt.gca()
        ax.set_ylim([0, 1.1])
        ax.axhline(y=1.0, linewidth=1, color="k", ls="--")

        plt.savefig(f"results/{name}-{alphas[alpha_ind]}.png")
        plt.show()
        plt.close()


